import React, { useState } from "react";
import "./Expense.css";
import ExpenseItems from "../Expenses/ExpenseItems";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";

function Expense(props) {
  const [filteredYear, setFilteredYear] = useState("2020");

  const filterChangeHandler = (newChanges) => {
    setFilteredYear(newChanges);
  };

  const filteredExpenses = props.items.filter((x) => {})

  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selectedYear={filteredYear}
          dropDownFilter={filterChangeHandler}
        />
        {/* map of each expense items */}
        {props.items.map((x) => (
          <ExpenseItems 
          key = {x.id}
          title={x.title} 
          amount={x.amount} 
          date={x.date} />
        ))}

        {/* expenseitems before converted to map  */}
        {/* <ExpenseItems
    date={props.items[0].date}
    amount={props.items[0].amount}
    title={props.items[0].title}
  ></ExpenseItems>
  <ExpenseItems
    date={props.items[1].date}
    amount={props.items[1].amount}
    title={props.items[1].title}
  ></ExpenseItems>
  <ExpenseItems
    date={props.items[2].date}
    amount={props.items[2].amount}
    title={props.items[2].title}
  ></ExpenseItems>
  <ExpenseItems
    date={props.items[3].date}
    amount={props.items[3].amount}
    title={props.items[3].title}
  ></ExpenseItems> */}
      </Card>
    </div>
  );
}

export default Expense;
